import React, { Component }  from 'react';
import { Table } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        items:[]
      }

    }
    componentDidMount() {
      fetch('/nba',  {
        headers: {
          "Access-Control-Allow-Origin": "*"
        }
      })
        .then(response => {
          return response.json();
        })   
        .then((data) => {
          for (let i = 0; i < data.length; i++) {
            this.setState({
              items: data.map(item => ({
                live: item.isGameActivated,
                team1Name: item.team1Name,
                team1Score: item.team1Score,
                team2Name: item.team2Name,
                team2Score: item.team2Score
              }))
            });
            console.log(data[i])
          }
        });      
    }

    changeColor(bool){
      if(bool === true)
      {
          this.setState({
              myDynamicColor: '#932727'
          })
      }if(bool === false)
      {
          this.setState({
              myDynamicColor: '#ffffff'
          })
      }
  }

    render() {
      return (
        <div className="col">
        <h1>Live NBA scores</h1>
        {this.state.items.map((item, i) => (
          <li key={i}>{item.team1Name} ({item.team1Score}) - {item.team2Name} ({item.team2Score})  -- (Live: {item.isGameActivated})</li>
        ))}
      </div>
      );
    }

  }
export default App;
