import React, { Component }  from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        data: null,
      };
    }
    componentDidMount() {
      fetch('http://localhost:8082/nba')
        .then(response => response.json())
        .then(data => this.setState({ hits: data.hits }));
    }

  }
export default App;

